import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';

function RecipeDetails() {
  const { id } = useParams();
  const [recipe, setRecipe] = useState(null);
  const [error, setError] = useState(null); // error state add

  useEffect(() => {
    fetch(`https://glorious-capybara-jjrw767jw6r7fwxq-5000.app.github.dev/api/recipes/${id}`)
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => setRecipe(data))
      .catch(error => setError(error)); // 에러 처리
  }, [id]);

  if (error) {
    return <div>Error: {error.message}</div>; // error message
  }

  if (!recipe) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>{recipe.name}</h1>
      <p>{recipe.instructions}</p>
      <ul>
        {recipe.ingredients.map((ingredient, index) => (
          <li key={index}>{ingredient}</li>
        ))}
      </ul>
      <Link to={`/edit/${id}`}>Edit Recipe</Link>
    </div>
  );
}

export default RecipeDetails;
